import React from 'react'

const QuestionTile = (props) =>(
<div>
    <p>{props.text}</p>
    <p><button value={true} style={!props.status?null: (props.status === props.answer)?{backgroundColor:'green'}:{backgroundColor:'red'}
    }onClick={(e) => props.handleClick(e.target.value,props.index)}>True</button>
       <button onClick={(e) => props.handleClick(e.target.value,props.index)} value={false}>False</button>
    </p>
</div>
)

export default QuestionTile
